        _burn(_from, _amount);
        _moveDelegates(_delegates[_from], address(0), _amount);
    }

    // The CAKE TOKEN!
    CakeToken public cake;


    constructor(
        CakeToken _cake
    ) public {
        cake = _cake;
    }

